#!/bin/bash

echo "Test1. Line of text terminated by a new line."
./echo_client echo 127.0.0.1 8080 << 'END'
line1
line2
END
echo ""

echo "Test2. Line of text terminated by maximum length."
./echo_client echo 127.0.0.1 8080 << 'END'
"1000""2000""3000""4000"aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa"4096""5000"
END
echo ""

echo "Test3. No character and EOF."
./echo_client echo 127.0.0.1 8080 << 'END'

END
echo ""

echo "Test4. Client terminated after entering text."
./echo_client echo 127.0.0.1 8080 << 'END'
text
END
echo ""